import React, { useEffect, useState } from "react";
import { CiEdit } from "react-icons/ci";
import { RiDeleteBinLine } from "react-icons/ri";
import * as s from "./style";
import { useNavigate, useParams } from "react-router-dom";
import { addDoc, collection, Timestamp } from "firebase/firestore";
import { dbService } from "fBase";
import { Cookies } from "react-cookie";
import FormatDate from "component/Date";
import axios from "axios";
import { getCookie } from "cookies";
import { getComment } from "api/writingAPI";

const Detail = ({ user, userPlace }) => {
  const cookie = new Cookies();
  const Token = cookie.get("token");
  const username = cookie.get("username");
  // 쿠키에 담긴 userId
  const userId = cookie.get("userId");

  // 페이지 정보
  const [commentInput, setCommentInput] = useState("");
  // 댓글
  const [comment, setComment] = useState([]);
  // 특정 게시물 불러오기
  const [data, setData] = useState(null);

  const navigate = useNavigate();
  const { id } = useParams();
  // 게시글의 id와 주소의 id값이 같은 것을 찾는다 그것이 특정 게시물의 정보이다.
  const MyPlace = userPlace.find((user) => user.id === id);

  console.log("게시글", userPlace);

  // read 댓글 정보 보여주기
  useEffect(() => {
    const getPlaceInfo = async () => {
      const res = await getComment();
      setComment(res.data.comment);
    };
    getPlaceInfo();
  }, []);
  console.log("댓글 정보", comment);
  // 생성
  const onComment = async () => {
    try {
      const now = new Date(Date.now());

      const res = await axios.post(
        "http://localhost:8000/api/places/comment",
        {
          comment: commentInput,
          createdAt: now,
          creator: userId,
          username: username,
        },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${getCookie("token")}`,
          },
        }
      );

      console.log("댓글 생성 성공", res);
    } catch (error) {
      alert(error.response.data.message);
      console.log(error);
    }
  };

  const onCommentChange = (e) => {
    setCommentInput(e.target.value);
  };
  // console.log(commentInput);

  // 댓글 삭제
  const onDeleteComment = async (id) => {
    // const ok = window.confirm("댓글을 삭제 하시겠습니까?");
    // if (ok) {
    //   const commentRef = doc(dbService, "comment", id);
    //   try {
    //     await deleteDoc(commentRef);
    //     // console.log("삭제 완료");
    //   } catch (error) {
    //     // console.log(error);
    //   }
    // }
  };

  const CommentonClick = async () => {
    const now = new Date(Date.now());

    try {
      const sweetObj = {
        displayName: user.displayName,
        comment: commentInput,
        createdAt: Timestamp.fromDate(now),
        uid: user.uid,
        attachmentUrl: user.photoURL,
        // uid,
      };
      await addDoc(collection(dbService, "comment"), sweetObj);
      setCommentInput("");
    } catch (error) {
      // console.log(error);
    }
  };

  return (
    <s.Container>
      {MyPlace && (
        <div>
          <s.DetailContainer>
            <div className="info">
              <h1>{MyPlace.title}</h1>
              <s.DetailInfo>
                <div className="detailLine">
                  {MyPlace.attachmentUrl !== "" ? (
                    <s.DetailA to="/mypage">
                      이미지
                      {/* <s.DetailImg src={MyPlace?.attachmentUrl} alt="이미지" /> */}
                    </s.DetailA>
                  ) : (
                    ""
                  )}
                  <div className="name">
                    <s.DetailName href="/mypage">
                      {MyPlace.username}
                    </s.DetailName>
                    <s.DetailDate>
                      {MyPlace.createdAt}
                      {/* <FormatDate date={MyPlace.createdAt}></FormatDate> */}
                    </s.DetailDate>
                  </div>
                  {Token && MyPlace.creator === userId ? (
                    <s.InfoBtn
                      aria-label="edit_button"
                      border="#ccc"
                      color="#ccc"
                      hover="#282A3A"
                      hover_color="white"
                      margin="0.5rem"
                      onClick={() => navigate("/edit")}
                    >
                      <CiEdit />
                      Edit Article
                    </s.InfoBtn>
                  ) : (
                    ""
                  )}
                </div>
              </s.DetailInfo>
            </div>
          </s.DetailContainer>
          <s.DetailContent>
            <div>{MyPlace.content}</div>
            <s.DetailTag>
              <li>{MyPlace.tags}</li>
            </s.DetailTag>
          </s.DetailContent>
          {/* 댓글 창 */}
          <s.CommentContainer>
            {Token ? (
              <s.CommentText>
                <textarea
                  value={commentInput}
                  onChange={onCommentChange}
                  className="textArea"
                  type="text"
                  name="commentInput"
                  placeholder="Write a comment..."
                />
                <s.CommentPost>
                  <div className="commentName">
                    <s.DetailImg
                      src={user?.photoURL}
                      alt="이미지"
                      margin="1.25rem"
                      width_hover="28px"
                      height_hover="28px"
                    />
                    <span>{user?.displayName}</span>
                  </div>
                  <s.CommentBtn aria-label="comment_button" onClick={onComment}>
                    Comment
                  </s.CommentBtn>
                </s.CommentPost>
              </s.CommentText>
            ) : (
              <div>로그인 하신 후에 댓글을 사용하실 수 있습니다.</div>
            )}
          </s.CommentContainer>
          {/* 댓글 내용 */}
          {/* <s.CcommentContainer>
            <div>
              {comment.map((item, key) =>
                id === item?.uid ? (
                  // filter ? (
                  <s.CcommentTitle key={key}>
                    <s.CcommentDiv>
                      <p>{item.comment}</p>
                    </s.CcommentDiv>
                    <s.CommentPost>
                      <div className="commentName">
                        <s.DetailImg
                          src={item?.attachmentUrl}
                          alt="이미지"
                          margin="1.25rem"
                          width="24px"
                          height="24px"
                          width_hover="28px"
                          height_hover="28px"
                        />
                        <span>{item.displayName}</span>
                      </div>
                      {item.uid === user.uid ? (
                        <s.CcommentDelete
                          aria-label="delete_button"
                          onClick={() => onDeleteComment(item?.id)}
                        >
                          <RiDeleteBinLine />
                        </s.CcommentDelete>
                      ) : (
                        ""
                      )}
                    </s.CommentPost>
                  </s.CcommentTitle>
                ) : (
                  ""
                )
              )}
            </div>
          </s.CcommentContainer> */}
        </div>
      )}
    </s.Container>
  );
};

export default Detail;
