import { useForm } from "react-hook-form";
import { useNavigate } from "react-router-dom";
import * as s from "./style";
import axios from "axios";
import { useState } from "react";
import imageCompression from "browser-image-compression";

const Signup = () => {
  const [file, setFile] = useState();
  // 이코드를 수정해도 백엔드 때문에 이미지가 기본 이미지가 박힌다.그렇기에 이 코드 없어도 될거 같다.
  const basic =
    "https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png";
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();
  const navigate = useNavigate();

  // 포스트맨으로 이미지를 보내던지 이미지를 안보내도 기본 이미지가 보내진다.
  //
  const handleUpload = async () => {
    try {
      const formData = new FormData();

      formData.append("avatar", file);
      formData.append("email", watch("email"));
      formData.append("password", watch("password"));
      formData.append("username", watch("username"));

      const res = await axios.post(
        "http://localhost:8000/api/users/signup",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      console.log("회원 가입 성공", res);
      // console.log("formData 성공", formData);
      navigate("/login");
    } catch (err) {
      console.log(err);
      // alert(err.response.data.message);
    }
  };
  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };
  // console.log(file);

  const handleDeletePreview = () => {
    setFile(null);
  };
  //

  const onChangeImg = (e) => {
    e.preventDefault();
    const formData = new FormData();

    if (e.target.files) {
      const uploadFile = e.target.files[0];
      formData.append("file", uploadFile);
      setFile(uploadFile);
      console.log(uploadFile);
      console.log("===useState===");
      console.log(file);
    }
  };

  const onClickLogin2 = (e) => {
    e.preventDefault();
    const formData = new FormData();

    formData.append("file", file);
    formData.append("email", watch("email"));
    formData.append("password", watch("password"));
    formData.append("username", watch("username"));
    axios({
      method: "post",
      url: "http://localhost:8000/api/users/signup",
      data: formData,
    })
      .then((result) => {
        console.log("요청성공");
        console.log(result);
      })
      .catch((error) => {
        console.log("요청실패");
        console.log(error);
      });
  };

