import { useForm } from "react-hook-form";
import { useNavigate } from "react-router-dom";
import * as s from "./style";
import axios from "axios";
import { useState } from "react";

const Signup = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();
  const navigate = useNavigate();

  const onSubmit = async (data) => {
    try {
      axios
        .post("http://localhost:8000/api/users/signup", data, {
          headers: { "Content-Type": "application/json" },
        })
        .then((res) => console.log("회원 가입쪽?", res));
      navigate("/login");
    } catch (err) {
      console.log(err);
    }
  };
  const [file, setFile] = useState(null);
  // 이미지 업로드
  const handleUpload = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append("avatar", file);

    await axios
      .post("http://localhost:8000/api/users/signup", formData, {
        headers: {
          // "Content-Type": "multipart/form-data", 합치게 되면 이걸 없애고 form태그에 적어주자
        },
      })
      .then((res) => {
        console.log("Upload 성공", res);
        console.log("formData 성공", formData);
      })
      .catch((error) => {
        console.error("Upload 실패:", error);
      });
  };
  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  console.log("file 정보", file);
  return (
    <s.Container>
      <form
        action="/upload"
        // encType="multipart/form-data"
        onSubmit={handleUpload}
      >
        {/* <form onSubmit={handleUpload}> */}
        <input type="file" name="avatar" onChange={handleFileChange} />
        <input type="submit" />
      </form>
      <s.Sign>
        <h1>Sign up</h1>
        <s.SignupLink to="/login">
          <p className="signup">Have an account?</p>
        </s.SignupLink>
      </s.Sign>
      <s.FormContainer>
        <form onSubmit={handleSubmit(onSubmit)}>
          <input
            type="text"
            className="Input"
            placeholder="Username"
            {...register("username", {
              required: "4자 이상 12자리 이하의 username을 입력해 주세요",
              minLength: {
                value: 4,
                message: "4자 이상의 username을 입력해 주세요",
              },
              maxLength: {
                value: 12,
                message: "12자 이하의 usename을 입력해 주세요",
              },
            })}
          />
          <span>{errors?.username?.message}</span>
          <input
            type="email"
            className="Input"
            placeholder="Email"
            {...register("email", {
              required: "12자 이상 20자 이하의 email을 입력해 주세요",
              minLength: {
                value: 12,
                message: "12자 이상의 email을 입력해 주세요",
              },
              maxLength: {
                value: 20,
                message: "20자 이하의 email을 입력해 주세요",
              },
            })}
          />
          <span>{errors?.email?.message}</span>
          <input
            type="password"
            className="Input"
            placeholder="Password"
            {...register("password", {
              required: "8자 이상 12자 이하의 password를 입력해 주세요",
              minLength: {
                value: 8,
                message: "8자 이상의 password를 입력해 주세요",
              },
              maxLength: {
                value: 12,
                message: "12자 이하의 password를 입력해 주세요",
              },
            })}
          />
          <span>{errors?.password?.message}</span>
          <button>Sign up</button>
        </form>
      </s.FormContainer>
    </s.Container>
  );
};
export default Signup;
