import React, { useEffect, useState } from "react";
import { CiEdit } from "react-icons/ci";
import { RiDeleteBinLine } from "react-icons/ri";
import * as s from "./style";
import { useNavigate, useParams } from "react-router-dom";
import {
  addDoc,
  collection,
  deleteDoc,
  doc,
  getDoc,
  onSnapshot,
  orderBy,
  query,
  Timestamp,
} from "firebase/firestore";
import { dbService } from "fBase";
import { Cookies } from "react-cookie";

const Detail = ({ user }) => {
  const cookie = new Cookies();
  const Token = cookie.get("token");
  // 페이지 정보
  const [commentInput, setCommentInput] = useState("");
  // 댓글
  const [comment, setComment] = useState([]);
  // 특정 게시물 불러오기
  const [data, setData] = useState(null);

  const navigate = useNavigate();
  const { ids } = useParams();

  // 특정 게시물 페이지 불러오기 id
  useEffect(() => {
    const fetchData = async () => {
      const docRef = doc(dbService, "editor", ids);
      const docSnap = await getDoc(docRef);
      if (docSnap.exists()) {
        setData(docSnap.data());
      } else {
        // console.log("No such document!");
      }
    };
    fetchData();
  }, [ids]);

  // console.log("댓글", comment);

  // read 댓글 정보 보여주기
  useEffect(() => {
    const q = query(
      collection(dbService, "comment"),
      orderBy("createdAt", "desc")
    );
    onSnapshot(q, (snapshot) => {
      const nweetArrs = snapshot.docs.map((document) => ({
        id: document.id,
        ...document.data(),
      }));

      setComment(nweetArrs);
    });
  }, []);

  const onCommentChange = (e) => {
    setCommentInput(e.target.value);
  };

  // 댓글 삭제
  const onDeleteComment = async (id) => {
    const ok = window.confirm("댓글을 삭제 하시겠습니까?");
    if (ok) {
      const commentRef = doc(dbService, "comment", id);
      try {
        await deleteDoc(commentRef);
        // console.log("삭제 완료");
      } catch (error) {
        // console.log(error);
      }
    }
  };

  const CommentonClick = async () => {
    const now = new Date(Date.now());

    try {
      const sweetObj = {
        displayName: user.displayName,
        comment: commentInput,
        createdAt: Timestamp.fromDate(now),
        uid: user.uid,
        attachmentUrl: user.photoURL,
        ids,
      };
      await addDoc(collection(dbService, "comment"), sweetObj);
      setCommentInput("");
    } catch (error) {
      // console.log(error);
    }
  };
  // Firebase Timestamp 객체를 JavaScript Date 객체로 변환 createdAt 변환
  const formatDate = (date) => {
    const jsDate = date.toDate();
    const year = jsDate.getFullYear();
    const month = String(jsDate.getMonth() + 1).padStart(2, "0");
    const day = String(jsDate.getDate()).padStart(2, "0");
    const hours = String(jsDate.getHours()).padStart(2, "0");
    const minutes = String(jsDate.getMinutes()).padStart(2, "0");
    return `${year}.${month}.${day} ${hours}:${minutes}`;
  };

  return (
    <s.Container>
      {data && (
        <div>
          <s.DetailContainer>
            <div className="info">
              <h1>{data.title}</h1>
              <s.DetailInfo>
                <div className="detailLine">
                  {data.attachmentUrl !== "" ? (
                    <s.DetailA to="/mypage">
                      <s.DetailImg src={data?.attachmentUrl} alt="이미지" />
                    </s.DetailA>
                  ) : (
                    ""
                  )}
                  <div className="name">
                    <s.DetailName href="/mypage">
                      {data.displayName}
                    </s.DetailName>
                    <s.DetailDate>{formatDate(data.createdAt)}</s.DetailDate>
                  </div>
                  {Token && data.uid === user.uid ? (
                    <s.InfoBtn
                      border="#ccc"
                      color="#ccc"
                      hover="#282A3A"
                      hover_color="white"
                      margin="0.5rem"
                      onClick={() => navigate("/edit")}
                    >
                      <CiEdit />
                      Edit Article
                    </s.InfoBtn>
                  ) : (
                    ""
                  )}
                </div>
              </s.DetailInfo>
            </div>
          </s.DetailContainer>
          <s.DetailContent>
            <div>{data.content}</div>
            <s.DetailTag>
              <li>{data.tags}</li>
            </s.DetailTag>
          </s.DetailContent>
          {/* 댓글 창 */}
          <s.CommentContainer>
            {Token ? (
              <s.CommentText>
                <textarea
                  value={commentInput}
                  onChange={onCommentChange}
                  className="textArea"
                  type="text"
                  name="commentInput"
                  placeholder="Write a comment..."
                />
                <s.CommentPost>
                  <div className="commentName">
                    <s.DetailImg
                      src={user?.photoURL}
                      alt="이미지"
                      margin="1.25rem"
                      width_hover="28px"
                      height_hover="28px"
                    />
                    <span>{user?.displayName}</span>
                  </div>
                  <s.CommentBtn onClick={CommentonClick}>Comment</s.CommentBtn>
                </s.CommentPost>
              </s.CommentText>
            ) : (
              <div>로그인하신 후에 댓글을 사용하실 수 있습니다.</div>
            )}
          </s.CommentContainer>
          {/* 댓글 내용 */}
          <s.CcommentContainer>
            <div>
              {comment.map((item, key) =>
                ids === item?.ids ? (
                  // filter ? (
                  <s.CcommentTitle key={key}>
                    <s.CcommentDiv>
                      <p>{item.comment}</p>
                    </s.CcommentDiv>
                    <s.CommentPost>
                      <div className="commentName">
                        <s.DetailImg
                          src={item?.attachmentUrl}
                          alt="이미지"
                          margin="1.25rem"
                          width="24px"
                          height="24px"
                          width_hover="28px"
                          height_hover="28px"
                        />
                        <span>{item.displayName}</span>
                      </div>
                      {item.uid === user.uid ? (
                        <s.CcommentDelete
                          onClick={() => onDeleteComment(item?.id)}
                        >
                          <RiDeleteBinLine />
                        </s.CcommentDelete>
                      ) : (
                        ""
                      )}
                    </s.CommentPost>
                  </s.CcommentTitle>
                ) : (
                  ""
                )
              )}
            </div>
          </s.CcommentContainer>
        </div>
      )}
    </s.Container>
  );
};

export default Detail;
