  const Articles = userPlace.find((el) => el.creator === userId);
  console.log("Articles", Articles);

  const mainCurrent = (index) => {
    setMenu(index);
  };

  // 작성한 글 삭제
  const onDeletePage = async (id) => {
    if (!window.confirm("삭제 하시겠습니까?")) {
      alert("취소하였습니다.");
